import React, { useState } from 'react';

function CalendarView({ tasks, onTaskClick }) {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);

  const formatDate = (date) => date.toISOString().split('T')[0];

  const firstDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
  const lastDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0);
  const startDay = firstDayOfMonth.getDay();

  const daysInMonth = lastDayOfMonth.getDate();
  const totalCells = Math.ceil((startDay + daysInMonth) / 7) * 7;

  const tasksByDate = tasks.reduce((acc, task) => {
    if (!task.dueDate) return acc;
    if (!acc[task.dueDate]) acc[task.dueDate] = [];
    acc[task.dueDate].push(task);
    return acc;
  }, {});

  const prevMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() - 1, 1));
    setSelectedDate(null);
  };

  const nextMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 1));
    setSelectedDate(null);
  };

  const handleDayClick = (date) => {
    setSelectedDate(date);
  };

  const calendarCells = [];
  for (let i = 0; i < totalCells; i++) {
    const dayNumber = i - startDay + 1;
    let cellDate = null;
    if (dayNumber > 0 && dayNumber <= daysInMonth) {
      cellDate = new Date(currentDate.getFullYear(), currentDate.getMonth(), dayNumber);
    }
    calendarCells.push(cellDate);
  }

  const weekdays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  return (
    <section className="calendar-view" style={{ width: '100%', height: '100vh', padding: '20px', boxSizing: 'border-box', display: 'flex', flexDirection: 'column' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px', backgroundColor: '#FCFCFC', border: '1px solid #ccc', borderRadius: '8px', padding: '10px 20px' }}>
        <h2 style={{ margin: 0 }}>
          {currentDate.toLocaleString('default', { month: 'long' })} {currentDate.getFullYear()}
        </h2>
      </div>

     <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px', gap: '30px', fontSize: '32px', fontWeight: 'bold' }}>
  <span style={{ cursor: 'pointer', userSelect: 'none' }} onClick={prevMonth}>&lt;</span>
  <strong style={{ fontSize: '24px' }}>
    {currentDate.toLocaleString('default', { month: 'long' })} {currentDate.getFullYear()}
  </strong>
  <span style={{ cursor: 'pointer', userSelect: 'none' }} onClick={nextMonth}>&gt;</span>
</div>


      <div className="calendar-grid" style={{ gridTemplateColumns: 'repeat(7, 1fr)', flexGrow: 1, display: 'grid', gap: '5px', overflowY: 'auto' }}>
        {weekdays.map((day) => (
          <div key={day} style={{ fontWeight: 'bold', textAlign: 'center', padding: '5px 0' }}>
            {day}
          </div>
        ))}

        {calendarCells.map((date, idx) => {
          if (!date) {
            return <div key={idx} className="calendar-day" style={{ backgroundColor: '#f0f0f0' }}></div>;
          }

          const dateStr = formatDate(date);
          const dayTasks = tasksByDate[dateStr] || [];
          const isSelected = selectedDate && dateStr === formatDate(selectedDate);

          return (
            <div
              key={dateStr}
              className="calendar-day"
              onClick={() => handleDayClick(date)}
              style={{
                minHeight: '80px',
                textAlign: 'left',
                padding: '5px',
                overflowY: 'auto',
                cursor: 'pointer',
                backgroundColor: isSelected ? '#E94E4E' : '#FCFCFC',
                color: isSelected ? '#fff' : '#000',
                borderRadius: '8px',
                userSelect: 'none',
              }}
            >
              <strong>{date.getDate()}</strong>
              {dayTasks.map((task) => (
                <div
                  key={task.id}
                  className="task-box"
                  title={task.title}
                  style={{ backgroundColor: '#fff', color: '#000', padding: '2px 4px', borderRadius: '4px', marginTop: '4px', fontSize: '12px', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis', cursor: 'pointer' }}
                  onClick={(e) => {
                    e.stopPropagation();
                    if (typeof onTaskClick === 'function') {
                      onTaskClick(task);
                    }
                  }}
                >
                  {task.title}
                </div>
              ))}
            </div>
          );
        })}
      </div>
    </section>
  );
}

export default CalendarView;
