import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Login({ onLogin }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!email || !password) {
      setError('Please enter both email and password.');
      return;
    }
    // Simple email format validation
    const emailRegex = /^[\w.-]+@([\w-]+\.)+[\w-]{2,4}$/;
    if (!emailRegex.test(email)) {
      setError('Please enter a valid email address.');
      return;
    }

    // Check user credentials from localStorage
    const users = JSON.parse(localStorage.getItem('users') || '[]');
    const user = users.find((u) => u.email === email && u.password === password);
    if (!user) {
      setError('Invalid email or password.');
      return;
    }

    setError('');
    onLogin(email);
  };

  return (
    <div style={{ display: 'flex', minHeight: '100vh', fontFamily: 'Arial, sans-serif' }}>
      {/* Left panel */}
      <div style={{
        flex: 1,
        background: 'linear-gradient(180deg, #6ec1ff 0%, #0072ff 100%)',
        color: 'white',
        padding: '40px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        borderTopLeftRadius: '8px',
        borderBottomLeftRadius: '8px'
      }}>
        <div>
          <h2 style={{ fontWeight: 'bold', fontSize: '2rem', marginBottom: '20px' }}>COMPANY LOGO</h2>
          <h1 style={{ fontWeight: 'bold', fontSize: '2.5rem', marginBottom: '20px' }}>Welcome to...</h1>
          <p style={{ fontSize: '1rem', lineHeight: '1.5' }}>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
          </p>
        </div>
        <div style={{ fontSize: '0.9rem' }}>
          Lorem ipsum dolor sit amet
        </div>
      </div>

      {/* Right panel */}
      <div style={{
        flex: 1,
        background: 'white',
        padding: '60px 40px',
        borderTopRightRadius: '8px',
        borderBottomRightRadius: '8px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center'
      }}>
        <h2 style={{ color: '#0072ff', marginBottom: '10px' }}>Login</h2>
        <p style={{ color: '#666', marginBottom: '30px' }}>Welcome! Login to get amazing discounts and offers only for you.</p>
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column' }}>
          <label htmlFor="email" style={{ marginBottom: '5px', color: '#999' }}>User Name</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email"
            style={{
              padding: '12px',
              marginBottom: '20px',
              borderRadius: '4px',
              border: '1px solid #ccc',
              fontSize: '1rem'
            }}
          />
          <label htmlFor="password" style={{ marginBottom: '5px', color: '#999' }}>Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your password"
            style={{
              padding: '12px',
              marginBottom: '20px',
              borderRadius: '4px',
              border: '1px solid #ccc',
              fontSize: '1rem'
            }}
          />
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
            <input type="checkbox" id="remember" style={{ marginRight: '10px' }} />
            <label htmlFor="remember" style={{ color: '#666' }}>Remember me</label>
          </div>
          {error && <p style={{ color: 'red', marginBottom: '20px' }}>{error}</p>}
          <button type="submit" style={{
            backgroundColor: '#0072ff',
            color: 'white',
            padding: '15px',
            border: 'none',
            borderRadius: '4px',
            fontWeight: 'bold',
            fontSize: '1rem',
            cursor: 'pointer'
          }}>
            LOGIN
          </button>
        </form>
        <div style={{ marginTop: '20px', fontSize: '0.9rem', color: '#666', display: 'flex', justifyContent: 'space-between' }}>
          <span>New User? <Link to="/signup" style={{ color: '#0072ff', textDecoration: 'none' }}>Signup</Link></span>
          <span style={{ fontStyle: 'italic', color: '#aaa' }}>Forgot your password?</span>
        </div>
      </div>
    </div>
  );
}

export default Login;
