import React from 'react';

function CalendarView({ tasks, onAddTask, onNotificationClick, notificationCount }) {
  // For simplicity, this is a placeholder calendar header with month/year and notification bell

  const monthYear = 'May 2025'; // This can be dynamic based on current calendar state

  return (
    <div className="calendar-view">
      <header className="calendar-header" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '10px 20px', borderBottom: '1px solid #ccc' }}>
        <h2 style={{ margin: 0 }}>{monthYear}</h2>
        <div
          className="notification-bell"
          title="Notifications"
          onClick={onNotificationClick}
          style={{ position: 'relative', cursor: 'pointer' }}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
            width="28"
            height="28"
            strokeWidth={2}
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9"
            />
          </svg>
          {notificationCount > 0 && (
            <span
              className="notification-count"
              style={{
                position: 'absolute',
                top: '-5px',
                right: '-5px',
                backgroundColor: '#E94E4E',
                color: '#fff',
                borderRadius: '50%',
                padding: '2px 6px',
                fontSize: '12px',
                fontWeight: 'bold',
              }}
            >
              {notificationCount}
            </span>
          )}
        </div>
      </header>
      <div className="calendar-body" style={{ padding: '20px' }}>
        {/* Placeholder for calendar grid or content */}
        <p>Calendar content goes here...</p>
        <button onClick={() => onAddTask && onAddTask(new Date().toISOString().slice(0, 10))}>
          Add Task for Today
        </button>
      </div>
    </div>
  );
}

export default CalendarView;
