import React from 'react';

const statusOptions = ['incomplete', 'in progress', 'complete'];

function TaskList({ tasks, updateTask, deleteTask, currentUser, setEditingTask }) {
  const handleStatusChange = (task, e) => {
    updateTask({ ...task, status: e.target.value });
  };

  const isOverdue = (dueDate) => {
    const today = new Date();
    const due = new Date(dueDate);
    return due < today && dueDate !== '';
  };

  const getStatusClass = (status) => {
    switch (status) {
      case 'in progress':
        return 'inprogress';
      case 'complete':
        return 'complete';
      case 'incomplete':
      default:
        return 'incomplete';
    }
  };

  return (
    <div>
      {tasks.length === 0 ? (
        <p>No tasks to display.</p>
      ) : (
        tasks.map((task) => (
          <div key={task.id} className="task">
            <h3>{task.title}</h3>
            <p>Due Date: {task.dueDate}</p>
            <span className={`status ${getStatusClass(task.status)}`}>
              <select value={task.status} onChange={(e) => handleStatusChange(task, e)}>
                {statusOptions.map((status) => (
                  <option key={status} value={status}>
                    {status.charAt(0).toUpperCase() + status.slice(1)}
                  </option>
                ))}
              </select>
            </span>
          </div>
        ))
      )}
    </div>
  );
}

export default TaskList;
