import React from 'react';

function CalendarView({ tasks }) {
  // Group tasks by due date
  const tasksByDate = tasks.reduce((acc, task) => {
    if (!task.dueDate) return acc;
    if (!acc[task.dueDate]) acc[task.dueDate] = [];
    acc[task.dueDate].push(task);
    return acc;
  }, {});

  const sortedDates = Object.keys(tasksByDate).sort();

  return (
    <div style={{ backgroundColor: '#fff', padding: '15px', borderRadius: '8px', boxShadow: '0 0 5px rgba(0,0,0,0.1)' }}>
      <h2>Calendar View</h2>
      {sortedDates.length === 0 ? (
        <p>No tasks with due dates to display.</p>
      ) : (
        <div>
          {sortedDates.map((date) => (
            <div key={date} style={{ marginBottom: '20px' }}>
              <h3>{new Date(date).toLocaleDateString()}</h3>
              <ul>
                {tasksByDate[date].map((task) => (
                  <li key={task.id} style={{ marginBottom: '8px' }}>
                    <strong>{task.title}</strong> - {task.status.charAt(0).toUpperCase() + task.status.slice(1)} - Assigned to: {task.assignedTo}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default CalendarView;
