import React, { useState, useEffect } from 'react';

const urgencyLevels = ['low', 'medium', 'high'];
const statusOptions = ['incomplete', 'in progress', 'complete'];

function TaskForm({ addTask, currentUser, editingTask, updateTask, cancelEdit }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [urgency, setUrgency] = useState('medium');
  const [assignedTo, setAssignedTo] = useState(currentUser);
  const [dueDate, setDueDate] = useState('');
  const [status, setStatus] = useState('incomplete');
  const [error, setError] = useState('');

  useEffect(() => {
    if (editingTask) {
      setTitle(editingTask.title);
      setDescription(editingTask.description);
      setUrgency(editingTask.urgency);
      setAssignedTo(editingTask.assignedTo);
      setDueDate(editingTask.dueDate);
      setStatus(editingTask.status);
    } else {
      setTitle('');
      setDescription('');
      setUrgency('medium');
      setAssignedTo(currentUser);
      setDueDate('');
      setStatus('incomplete');
    }
  }, [editingTask, currentUser]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title || !dueDate) {
      setError('Please provide at least a title and due date.');
      return;
    }
    setError('');
    const taskData = {
      title,
      description,
      urgency,
      assignedTo,
      dueDate,
      status,
    };
    if (editingTask) {
      updateTask({ ...taskData, id: editingTask.id });
    } else {
      addTask(taskData);
    }
    // Reset form if adding new task
    if (!editingTask) {
      setTitle('');
      setDescription('');
      setUrgency('medium');
      setAssignedTo(currentUser);
      setDueDate('');
      setStatus('incomplete');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>{editingTask ? 'Edit Task' : 'Add New Task'}</h2>
      <label htmlFor="title">Task name*</label>
      <input
        id="title"
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Task title"
        required
      />

      <label htmlFor="dueDate">Due date*</label>
      <input
        id="dueDate"
        type="date"
        value={dueDate}
        onChange={(e) => setDueDate(e.target.value)}
        required
      />

      <label htmlFor="urgency">Priority</label>
      <select
        id="urgency"
        value={urgency}
        onChange={(e) => setUrgency(e.target.value)}
      >
        {urgencyLevels.map((level) => (
          <option key={level} value={level}>
            {level.charAt(0).toUpperCase() + level.slice(1)}
          </option>
        ))}
      </select>

      <button type="submit">{editingTask ? 'Update Task' : 'Submit'}</button>
      {editingTask && (
        <button type="button" onClick={cancelEdit} style={{ marginLeft: '10px' }}>
          Cancel
        </button>
      )}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </form>
  );
}

export default TaskForm;
