import React, { useState, useEffect } from 'react';
import TaskList from './TaskList';
import TaskForm from './TaskForm';
import CalendarView from './CalendarView';
import Settings from './Settings';

const initialTasks = [
  {
    id: 1,
    title: 'Complete project proposal',
    description: 'Finish writing the project proposal document',
    status: 'in progress',
    urgency: 'high',
    assignedTo: 'user1@example.com',
    dueDate: '2024-06-10',
  },
  {
    id: 2,
    title: 'Review team feedback',
    description: 'Analyze feedback from QA team',
    status: 'incomplete',
    urgency: 'medium',
    assignedTo: 'user2@example.com',
    dueDate: '2024-06-12',
  },
];

function TaskDashboard({ user, onLogout }) {
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem('tasks');
    return saved ? JSON.parse(saved) : initialTasks;
  });
  const [filterUser, setFilterUser] = useState('all');
  const [view, setView] = useState('list'); // 'list', 'calendar', 'create', 'settings'
  const [searchTerm, setSearchTerm] = useState('');
  const [editingTask, setEditingTask] = useState(null);

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  const addTask = (task) => {
    setTasks((prev) => [...prev, { ...task, id: Date.now() }]);
  };

  const updateTask = (updatedTask) => {
    setTasks((prev) => prev.map((t) => (t.id === updatedTask.id ? updatedTask : t)));
    setEditingTask(null);
  };

  const deleteTask = (id) => {
    setTasks((prev) => prev.filter((t) => t.id !== id));
  };

  const filteredTasks =
    tasks
      .filter((t) => (filterUser === 'all' ? true : t.assignedTo === filterUser))
      .filter((t) =>
        t.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        t.description.toLowerCase().includes(searchTerm.toLowerCase())
      );

  const uniqueUsers = Array.from(new Set(tasks.map((t) => t.assignedTo)));

  return (
    <>
      <header>
        <h1>TASKOMATIC</h1>
        <nav>
          <ul>
            <li><a href="#" onClick={(e) => { e.preventDefault(); setView('list'); }} className={view === 'list' ? 'active' : ''}>My tasks</a></li>
            <li><a href="#" onClick={(e) => { e.preventDefault(); setView('calendar'); }} className={view === 'calendar' ? 'active' : ''}>Calendar</a></li>
            <li><a href="#" onClick={(e) => { e.preventDefault(); setView('create'); setEditingTask(null); }} className={view === 'create' ? 'active' : ''}>Create Task</a></li>
            <li><a href="#" onClick={(e) => { e.preventDefault(); setView('settings'); }} className={view === 'settings' ? 'active' : ''}>Settings</a></li>
            <li className="user">{user.email}</li>
            <li><a href="#" className="logout" onClick={(e) => { e.preventDefault(); onLogout(); }}>Logout</a></li>
          </ul>
        </nav>
      </header>

      <main>
        {view === 'list' && (
          <>
            <section className="tasks">
              <h2>My Tasks</h2>
              <TaskList
                tasks={filteredTasks}
                updateTask={updateTask}
                deleteTask={deleteTask}
                currentUser={user.email}
                setEditingTask={setEditingTask}
              />
            </section>
            <section className="add-task">
              <TaskForm
                addTask={addTask}
                currentUser={user.email}
                editingTask={editingTask}
                updateTask={updateTask}
                cancelEdit={() => setEditingTask(null)}
              />
            </section>
          </>
        )}
        {view === 'calendar' && <CalendarView tasks={filteredTasks} />}
        {view === 'create' && (
          <section className="add-task">
            <TaskForm
              addTask={addTask}
              currentUser={user.email}
              editingTask={editingTask}
              updateTask={updateTask}
              cancelEdit={() => setEditingTask(null)}
            />
          </section>
        )}
        {view === 'settings' && <Settings />}
      </main>
    </>
  );
}

export default TaskDashboard;
