import React, { useState } from 'react';

function CalendarView({ tasks }) {
  const [currentDate, setCurrentDate] = useState(new Date());

  // Get first day of the month
  const firstDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
  // Get last day of the month
  const lastDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0);
  // Get day of week for first day (0=Sunday, 6=Saturday)
  const startDay = firstDayOfMonth.getDay();

  // Generate array of dates to display in calendar grid (including previous month's trailing days)
  const daysInMonth = lastDayOfMonth.getDate();
  const totalCells = Math.ceil((startDay + daysInMonth) / 7) * 7;

  // Helper to format date to YYYY-MM-DD
  const formatDate = (date) => date.toISOString().split('T')[0];

  // Group tasks by due date
  const tasksByDate = tasks.reduce((acc, task) => {
    if (!task.dueDate) return acc;
    if (!acc[task.dueDate]) acc[task.dueDate] = [];
    acc[task.dueDate].push(task);
    return acc;
  }, {});

  // Handlers for month navigation
  const prevMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() - 1, 1));
  };

  const nextMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 1));
  };

  // Generate calendar cells
  const calendarCells = [];
  for (let i = 0; i < totalCells; i++) {
    const dayNumber = i - startDay + 1;
    let cellDate = null;
    if (dayNumber > 0 && dayNumber <= daysInMonth) {
      cellDate = new Date(currentDate.getFullYear(), currentDate.getMonth(), dayNumber);
    }
    calendarCells.push(cellDate);
  }

  // Weekday labels
  const weekdays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  return (
    <section className="calendar-view" style={{ width: '100vw', height: '100vh', padding: '20px', boxSizing: 'border-box' }}>
      <h2>
        {currentDate.toLocaleString('default', { month: 'long' })} {currentDate.getFullYear()}
      </h2>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px' }}>
        <button onClick={prevMonth}>Previous</button>
        <button onClick={nextMonth}>Next</button>
      </div>
      <div className="calendar-grid" style={{ gridTemplateColumns: 'repeat(7, 1fr)', height: 'calc(100% - 100px)', display: 'grid', gap: '5px' }}>
        {weekdays.map((day) => (
          <div key={day} style={{ fontWeight: 'bold', textAlign: 'center', padding: '5px 0' }}>
            {day}
          </div>
        ))}
        {calendarCells.map((date, idx) => {
          if (!date) {
            return <div key={idx} className="calendar-day" style={{ backgroundColor: '#f0f0f0' }}></div>;
          }
          const dateStr = formatDate(date);
          const dayTasks = tasksByDate[dateStr] || [];
          return (
            <div key={dateStr} className="calendar-day" style={{ minHeight: '80px', textAlign: 'left', padding: '5px', overflowY: 'auto' }}>
              <strong>{date.getDate()}</strong>
              {dayTasks.map((task) => (
                <div key={task.id} className="task-box" title={task.title}>
                  {task.title}
                </div>
              ))}
            </div>
          );
        })}
      </div>
    </section>
  );
}

export default CalendarView;
