import React, { useState, useEffect } from 'react';
import TaskList from './TaskList';
import TaskForm from './TaskForm';
import CalendarView from './CalendarView';

const initialTasks = [
  {
    id: 1,
    title: 'Complete project proposal',
    description: 'Finish writing the project proposal document',
    status: 'in progress',
    urgency: 'high',
    assignedTo: 'user1@example.com',
    dueDate: '2024-06-10',
  },
  {
    id: 2,
    title: 'Review team feedback',
    description: 'Analyze feedback from QA team',
    status: 'incomplete',
    urgency: 'medium',
    assignedTo: 'user2@example.com',
    dueDate: '2024-06-12',
  },
];

function TaskDashboard({ user, onLogout }) {
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem('tasks');
    return saved ? JSON.parse(saved) : initialTasks;
  });
  const [filterUser, setFilterUser] = useState('all');
  const [view, setView] = useState('list'); // 'list' or 'calendar'
  const [searchTerm, setSearchTerm] = useState('');
  const [editingTask, setEditingTask] = useState(null);

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  const addTask = (task) => {
    setTasks((prev) => [...prev, { ...task, id: Date.now() }]);
  };

  const updateTask = (updatedTask) => {
    setTasks((prev) => prev.map((t) => (t.id === updatedTask.id ? updatedTask : t)));
    setEditingTask(null);
  };

  const deleteTask = (id) => {
    setTasks((prev) => prev.filter((t) => t.id !== id));
  };

  const filteredTasks =
    tasks
      .filter((t) => (filterUser === 'all' ? true : t.assignedTo === filterUser))
      .filter((t) =>
        t.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        t.description.toLowerCase().includes(searchTerm.toLowerCase())
      );

  const uniqueUsers = Array.from(new Set(tasks.map((t) => t.assignedTo)));

  return (
    <div>
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h1>Task Management Tracker</h1>
        <div>
          <span style={{ marginRight: '15px' }}>Logged in as: {user.email}</span>
          <button onClick={onLogout} style={{ padding: '6px 12px', cursor: 'pointer' }}>Logout</button>
        </div>
      </header>

      <div style={{ marginBottom: '15px' }}>
        <button onClick={() => setView('list')} disabled={view === 'list'} style={{ marginRight: '10px' }}>
          List View
        </button>
        <button onClick={() => setView('calendar')} disabled={view === 'calendar'}>
          Calendar View
        </button>
      </div>

      <div style={{ marginBottom: '15px' }}>
        <label htmlFor="filterUser" style={{ marginRight: '10px' }}>Filter by Assigned User:</label>
        <select
          id="filterUser"
          value={filterUser}
          onChange={(e) => setFilterUser(e.target.value)}
          style={{ marginRight: '20px' }}
        >
          <option value="all">All</option>
          {uniqueUsers.map((userEmail) => (
            <option key={userEmail} value={userEmail}>
              {userEmail}
            </option>
          ))}
        </select>

        <input
          type="text"
          placeholder="Search tasks..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: '6px 10px', borderRadius: '6px', border: '1px solid #ccc', width: '250px' }}
        />
      </div>

      {view === 'list' ? (
        <div style={{ maxHeight: '60vh', overflowY: 'auto' }}>
          <TaskForm addTask={addTask} currentUser={user.email} editingTask={editingTask} updateTask={updateTask} cancelEdit={() => setEditingTask(null)} />
          <TaskList
            tasks={filteredTasks}
            updateTask={updateTask}
            deleteTask={deleteTask}
            currentUser={user.email}
            setEditingTask={setEditingTask}
          />
        </div>
      ) : (
        <CalendarView tasks={filteredTasks} />
      )}
    </div>
  );
}

export default TaskDashboard;
