import React, { useState, useEffect } from 'react';

const urgencyLevels = ['low', 'medium', 'high'];
const statusOptions = ['incomplete', 'in progress', 'complete'];

function TaskForm({ addTask, currentUser, editingTask, updateTask, cancelEdit }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [urgency, setUrgency] = useState('medium');
  const [assignedTo, setAssignedTo] = useState(currentUser);
  const [dueDate, setDueDate] = useState('');
  const [status, setStatus] = useState('incomplete');
  const [error, setError] = useState('');

  useEffect(() => {
    if (editingTask) {
      setTitle(editingTask.title);
      setDescription(editingTask.description);
      setUrgency(editingTask.urgency);
      setAssignedTo(editingTask.assignedTo);
      setDueDate(editingTask.dueDate);
      setStatus(editingTask.status);
    } else {
      setTitle('');
      setDescription('');
      setUrgency('medium');
      setAssignedTo(currentUser);
      setDueDate('');
      setStatus('incomplete');
    }
  }, [editingTask, currentUser]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title || !dueDate) {
      setError('Please provide at least a title and due date.');
      return;
    }
    setError('');
    const taskData = {
      title,
      description,
      urgency,
      assignedTo,
      dueDate,
      status,
    };
    if (editingTask) {
      updateTask({ ...taskData, id: editingTask.id });
    } else {
      addTask(taskData);
    }
    // Reset form if adding new task
    if (!editingTask) {
      setTitle('');
      setDescription('');
      setUrgency('medium');
      setAssignedTo(currentUser);
      setDueDate('');
      setStatus('incomplete');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: '20px', backgroundColor: '#fff', padding: '15px', borderRadius: '12px', boxShadow: '0 0 10px rgba(0,0,0,0.1)' }}>
      <h2>{editingTask ? 'Edit Task' : 'Add New Task'}</h2>
      <div style={{ marginBottom: '10px' }}>
        <label htmlFor="title" style={{ display: 'block', marginBottom: '5px' }}>Title*</label>
        <input
          id="title"
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={{ width: '100%', padding: '10px', borderRadius: '8px', border: '1.5px solid #ddd' }}
          placeholder="Task title"
        />
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label htmlFor="description" style={{ display: 'block', marginBottom: '5px' }}>Description</label>
        <textarea
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          rows="3"
          style={{ width: '100%', padding: '10px', borderRadius: '8px', border: '1.5px solid #ddd' }}
          placeholder="Task description"
        />
      </div>
      <div style={{ marginBottom: '10px', display: 'flex', gap: '15px', flexWrap: 'wrap' }}>
        <div style={{ flex: '1 1 150px' }}>
          <label htmlFor="urgency" style={{ display: 'block', marginBottom: '5px' }}>Urgency</label>
          <select
            id="urgency"
            value={urgency}
            onChange={(e) => setUrgency(e.target.value)}
            style={{ width: '100%', padding: '10px', borderRadius: '8px', border: '1.5px solid #ddd' }}
          >
            {urgencyLevels.map((level) => (
              <option key={level} value={level}>{level.charAt(0).toUpperCase() + level.slice(1)}</option>
            ))}
          </select>
        </div>
        <div style={{ flex: '1 1 150px' }}>
          <label htmlFor="status" style={{ display: 'block', marginBottom: '5px' }}>Status</label>
          <select
            id="status"
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            style={{ width: '100%', padding: '10px', borderRadius: '8px', border: '1.5px solid #ddd' }}
          >
            {statusOptions.map((s) => (
              <option key={s} value={s}>{s.charAt(0).toUpperCase() + s.slice(1)}</option>
            ))}
          </select>
        </div>
        <div style={{ flex: '1 1 200px' }}>
          <label htmlFor="assignedTo" style={{ display: 'block', marginBottom: '5px' }}>Assign To (email)</label>
          <input
            id="assignedTo"
            type="email"
            value={assignedTo}
            onChange={(e) => setAssignedTo(e.target.value)}
            style={{ width: '100%', padding: '10px', borderRadius: '8px', border: '1.5px solid #ddd' }}
            placeholder="user@example.com"
          />
        </div>
        <div style={{ flex: '1 1 150px' }}>
          <label htmlFor="dueDate" style={{ display: 'block', marginBottom: '5px' }}>Due Date*</label>
          <input
            id="dueDate"
            type="date"
            value={dueDate}
            onChange={(e) => setDueDate(e.target.value)}
            style={{ width: '100%', padding: '10px', borderRadius: '8px', border: '1.5px solid #ddd' }}
          />
        </div>
      </div>
      {error && <p style={{ color: 'red', marginBottom: '10px' }}>{error}</p>}
      <div style={{ display: 'flex', gap: '10px' }}>
        <button type="submit" style={{ flex: 1 }}>
          {editingTask ? 'Update Task' : 'Add Task'}
        </button>
        {editingTask && (
          <button type="button" onClick={cancelEdit} style={{ flex: 1, backgroundColor: '#ccc', color: '#333' }}>
            Cancel
          </button>
        )}
      </div>
    </form>
  );
}

export default TaskForm;
