import React from 'react';

function CalendarView({ tasks }) {
  // Define the date range for the calendar (May 17 to May 22)
  const startDate = new Date('2024-05-17');
  const endDate = new Date('2024-05-22');

  // Create an array of dates in the range
  const dateArray = [];
  for (let d = new Date(startDate); d <= endDate; d.setDate(d.getDate() + 1)) {
    dateArray.push(new Date(d));
  }

  // Group tasks by due date
  const tasksByDate = tasks.reduce((acc, task) => {
    if (!task.dueDate) return acc;
    if (!acc[task.dueDate]) acc[task.dueDate] = [];
    acc[task.dueDate].push(task);
    return acc;
  }, {});

  // Format date to display as "May 17"
  const formatDateDisplay = (date) => {
    return date.toLocaleString('default', { month: 'short' }) + ' ' + date.getDate();
  };

  return (
    <section className="calendar-view">
      <h2>Calendar</h2>
      <div className="calendar-grid">
        {dateArray.map((date) => {
          const dateStr = date.toISOString().split('T')[0];
          const dayTasks = tasksByDate[dateStr] || [];
          return (
            <div key={dateStr} className="calendar-day">
              <strong>{formatDateDisplay(date)}</strong>
              {dayTasks.length === 0 ? (
                <div className="task-box">No Tasks</div>
              ) : (
                dayTasks.map((task) => (
                  <div key={task.id} className="task-box">
                    {task.title}
                  </div>
                ))
              )}
            </div>
          );
        })}
      </div>
    </section>
  );
}

export default CalendarView;
